import React, { useState } from 'react';
import './SearchBar.css';

const SearchBar = () => {
  const [prompt, setPrompt] = useState('');
  const [isFocused, setIsFocused] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (prompt.trim()) {
      console.log('AI Agent Prompt:', prompt);
      // Handle AI agent prompt here
      setPrompt('');
    }
  };

  return (
    <div className="search-container">
      <form className="search-form" onSubmit={handleSubmit}>
        <div className={`search-wrapper ${isFocused ? 'focused' : ''}`}>
          <textarea
            className="search-input"
            placeholder="Ask our AI agent about your dream destination..."
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            onFocus={() => setIsFocused(true)}
            onBlur={() => setIsFocused(false)}
            rows={1}
          />
          <button 
            type="submit" 
            className="search-button"
            disabled={!prompt.trim()}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M22 22L20 20M11.5 21C16.7467 21 21 16.7467 21 11.5C21 6.25329 16.7467 2 11.5 2C6.25329 2 2 6.25329 2 11.5C2 16.7467 6.25329 21 11.5 21Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
        </div>
      </form>
      <div className="search-suggestions">
        <p className="suggestions-text">Try: "Plan a 7-day trip to Japan" or "Best beaches in Europe"</p>
      </div>
    </div>
  );
};

export default SearchBar;
