import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './MyTrips.css';

const MyTrips = () => {
  const navigate = useNavigate();
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch trips from backend API
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const API_ENDPOINT = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/trip-planner';
        const response = await fetch(`${API_ENDPOINT}/trips`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setTrips(data.trips || []);
        } else {
          // If no trips or error, show empty state
          setTrips([]);
        }
      } catch (error) {
        console.error('Error fetching trips:', error);
        // Show empty state on error
        setTrips([]);
      } finally {
        setLoading(false);
      }
    };

    fetchTrips();
  }, []);

  // Sample trips for demonstration (remove when backend is ready)
  const sampleTrips = [
    {
      id: 1,
      destination: 'Tokyo, Japan',
      date: 'March 2024',
      status: 'Upcoming',
      image: 'https://images.unsplash.com/photo-1540959733332-eab4deabeeaf?w=400&h=300&fit=crop'
    },
    {
      id: 2,
      destination: 'Paris, France',
      date: 'May 2024',
      status: 'Planning',
      image: 'https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=400&h=300&fit=crop'
    },
    {
      id: 3,
      destination: 'Bali, Indonesia',
      date: 'July 2024',
      status: 'Upcoming',
      image: 'https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=400&h=300&fit=crop'
    },
    {
      id: 4,
      destination: 'New York, USA',
      date: 'September 2024',
      status: 'Planning',
      image: 'https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9?w=400&h=300&fit=crop'
    },
  ];

  const displayTrips = trips.length > 0 ? trips : sampleTrips;

  if (loading) {
    return (
      <div className="my-trips">
        <h2 className="trips-title">My Trips</h2>
        <div className="trips-loading">
          <p>Loading your trips...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="my-trips" id="my-trips">
      <h2 className="trips-title">My Trips</h2>
      {displayTrips.length === 0 ? (
        <div className="trips-empty">
          <p>You don't have any trips yet. Start planning your first adventure!</p>
        </div>
      ) : (
        <>
          <div className="trips-grid">
            {displayTrips.map((trip) => (
              <div key={trip.id} className="trip-card">
                <div className="trip-image-wrapper">
                  <img 
                    src={trip.image} 
                    alt={trip.destination}
                    className="trip-image"
                    loading="lazy"
                  />
                  <div className="trip-status-badge">{trip.status}</div>
                  <div className="trip-overlay">
                    <h3 className="trip-destination">{trip.destination}</h3>
                    <p className="trip-date">{trip.date}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="view-all-trips">
            <button 
              className="view-all-button"
              onClick={() => navigate('/trips')}
            >
              View All Trips & Chats
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default MyTrips;
