import React, { useState, useEffect } from 'react';
import './HeroBanner.css';

const HeroBanner = () => {
  const destinations = [
    {
      id: 1,
      name: 'Bali, Indonesia',
      image: 'https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=1200&h=600&fit=crop',
      description: 'Tropical paradise with stunning beaches'
    },
    {
      id: 2,
      name: 'Santorini, Greece',
      image: 'https://images.unsplash.com/photo-1613395877344-13d4a8e0d49e?w=1200&h=600&fit=crop',
      description: 'Breathtaking sunsets and white-washed buildings'
    },
    {
      id: 3,
      name: 'Tokyo, Japan',
      image: 'https://images.unsplash.com/photo-1540959733332-eab4deabeeaf?w=1200&h=600&fit=crop',
      description: 'Modern metropolis meets ancient tradition'
    },
    {
      id: 4,
      name: 'Paris, France',
      image: 'https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=1200&h=600&fit=crop',
      description: 'The City of Light and romance'
    },
    {
      id: 5,
      name: 'Maldives',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=1200&h=600&fit=crop',
      description: 'Crystal clear waters and overwater bungalows'
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % destinations.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [destinations.length]);

  const goToSlide = (index) => {
    setCurrentIndex(index);
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? destinations.length - 1 : prevIndex - 1
    );
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === destinations.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="hero-banner">
      <div className="banner-container">
        <div className="banner-slide">
          <img 
            src={destinations[currentIndex].image} 
            alt={destinations[currentIndex].name}
            className="banner-image"
          />
          <div className="banner-overlay">
            <div className="banner-content">
              <h2 className="banner-title">Most Popular Destinations</h2>
              <h3 className="destination-name">{destinations[currentIndex].name}</h3>
              <p className="destination-description">
                {destinations[currentIndex].description}
              </p>
            </div>
          </div>
        </div>

        <button className="banner-nav prev" onClick={goToPrevious}>
          ‹
        </button>
        <button className="banner-nav next" onClick={goToNext}>
          ›
        </button>

        <div className="banner-indicators">
          {destinations.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentIndex ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default HeroBanner;
