import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getTripsFromLocalStorage } from '../utils/tripUtils';
import './TripChat.css';

const TripChat = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [tripInfo, setTripInfo] = useState(null);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const API_ENDPOINT = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/trip-planner';

  // Fetch trip info and generate initial plan
  useEffect(() => {
    const fetchTripDataAndGeneratePlan = async () => {
      try {
        // First, try to get trip from localStorage
        const localTrips = getTripsFromLocalStorage();
        const localTrip = localTrips.find(trip => trip.id === tripId);
        
        if (localTrip) {
          setTripInfo({
            id: localTrip.id,
            title: localTrip.title,
            destination: localTrip.destination,
            image: localTrip.image,
            prompt: localTrip.prompt
          });
        }

        // Try to fetch from backend API
        try {
          const tripResponse = await fetch(`${API_ENDPOINT}/trips/${tripId}?userId=Kartik7`);
          if (tripResponse.ok) {
            const tripData = await tripResponse.json();
            setTripInfo(prev => prev || tripData);
          }
        } catch (err) {
          console.log('Could not fetch from backend, using local data');
        }

        // Fetch existing messages
        try {
          const messagesResponse = await fetch(`${API_ENDPOINT}/trips/${tripId}/messages?userId=Kartik7`);
          if (messagesResponse.ok) {
            const messagesData = await messagesResponse.json();
            if (messagesData.messages && messagesData.messages.length > 0) {
              setMessages(messagesData.messages);
              return; // Don't generate plan if messages already exist
            }
          }
        } catch (err) {
          console.log('Could not fetch messages from backend');
        }

        // If no messages exist and we have a prompt, generate the initial plan
        const tripPrompt = localTrip?.prompt || tripInfo?.prompt;
        if (tripPrompt && messages.length === 0) {
          await generateInitialTravelPlan(tripPrompt);
        }
      } catch (error) {
        console.error('Error fetching trip data:', error);
        // Use fallback data
        const localTrips = getTripsFromLocalStorage();
        const localTrip = localTrips.find(trip => trip.id === tripId);
        if (localTrip) {
          setTripInfo({
            id: localTrip.id,
            title: localTrip.title,
            destination: localTrip.destination,
            image: localTrip.image,
            prompt: localTrip.prompt
          });
          // Generate plan with local trip prompt
          if (localTrip.prompt) {
            await generateInitialTravelPlan(localTrip.prompt);
          }
        } else {
          setTripInfo({
            id: tripId,
            title: `Trip ${tripId}`,
            destination: 'Unknown',
            image: 'https://images.unsplash.com/photo-1488646953014-85cb44e25828?w=800&h=400&fit=crop'
          });
        }
      }
    };

    fetchTripDataAndGeneratePlan();
  }, [tripId]);

  // Generate initial comprehensive travel plan
  const generateInitialTravelPlan = async (prompt) => {
    setIsLoading(true);
    
    const comprehensivePrompt = `Based on the following travel request, create a detailed, day-by-day personalized travel plan. Format the response as a structured itinerary with bullet points for each day from start to end. Include:

1. **Travel/Transportation**: Flights, trains, buses, or other transportation for each day
2. **Hotels/Accommodations**: Recommended hotels with locations and brief descriptions
3. **Restaurants**: Dining recommendations for breakfast, lunch, and dinner each day
4. **Shopping**: Items to buy before the trip and shopping recommendations during the trip
5. **Medical Requirements**: Vaccinations, medications to take before going, health precautions
6. **Weather Forecast**: Expected weather conditions for each day of the trip

Original travel request: "${prompt}"

Please provide a comprehensive day-by-day itinerary in a clear, organized format with bullet points.`;

    try {
      const response = await fetch(`${API_ENDPOINT}/trips/${tripId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: 'Kartik7',
          tripId: tripId,
          message: comprehensivePrompt,
          timestamp: new Date().toISOString(),
          isInitialPlan: true
        })
      });

      if (response.ok) {
        const data = await response.json();
        const aiMessage = {
          id: Date.now(),
          role: 'assistant',
          content: data.response || data.message || data.plan || 'I\'m creating your personalized travel plan. Please wait...',
          timestamp: new Date().toISOString()
        };
        setMessages([aiMessage]);
      } else {
        // Fallback: Create a sample plan structure
        const samplePlan = createSamplePlan(prompt);
        const aiMessage = {
          id: Date.now(),
          role: 'assistant',
          content: samplePlan,
          timestamp: new Date().toISOString()
        };
        setMessages([aiMessage]);
      }
    } catch (error) {
      console.error('Error generating travel plan:', error);
      // Fallback: Create a sample plan structure
      const samplePlan = createSamplePlan(prompt);
      const aiMessage = {
        id: Date.now(),
        role: 'assistant',
        content: samplePlan,
        timestamp: new Date().toISOString()
      };
      setMessages([aiMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Create a sample plan structure if API fails
  const createSamplePlan = (prompt) => {
    return `# Your Personalized Travel Plan

Based on your request: "${prompt}"

## Pre-Trip Preparation

