import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './TripChat.css';

const TripChat = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [participants, setParticipants] = useState([]);
  const [tripInfo, setTripInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Fetch trip chat data
  useEffect(() => {
    const fetchChatData = async () => {
      try {
        const API_ENDPOINT = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/trip-planner';
        const response = await fetch(`${API_ENDPOINT}/trips/${tripId}/chat`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setMessages(data.messages || []);
          setParticipants(data.participants || []);
          setTripInfo(data.tripInfo || null);
        } else {
          // Use sample data if backend not available
          setSampleData();
        }
      } catch (error) {
        console.error('Error fetching chat data:', error);
        setSampleData();
      } finally {
        setLoading(false);
      }
    };

    fetchChatData();
  }, [tripId]);

  const setSampleData = () => {
    setMessages([
      {
        id: 1,
        sender: 'You',
        text: 'Let\'s finalize the hotel bookings for Tokyo',
        timestamp: new Date(Date.now() - 3600000).toISOString(),
        isOwn: true
      },
      {
        id: 2,
        sender: 'Sarah',
        text: 'I found a great hotel near Shibuya station. Should I book it?',
        timestamp: new Date(Date.now() - 3300000).toISOString(),
        isOwn: false
      },
      {
        id: 3,
        sender: 'Mike',
        text: 'That sounds perfect! What about the dates?',
        timestamp: new Date(Date.now() - 3000000).toISOString(),
        isOwn: false
      },
    ]);
    setParticipants([
      { id: 1, name: 'You', avatar: null },
      { id: 2, name: 'Sarah', avatar: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=100&h=100&fit=crop' },
      { id: 3, name: 'Mike', avatar: 'https://images.unsplash.com/photo-1506794778202-cad84cf45f1d?w=100&h=100&fit=crop' },
    ]);
    setTripInfo({
      prompt: "I'm planning a 10-day trip to Japan in March 2024. I have diabetes and need to ensure medical facilities are accessible.",
      timestamp: '2024-01-15T10:30:00.000Z'
    });
  };

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    const message = {
      id: Date.now(),
      sender: 'You',
      text: newMessage.trim(),
      timestamp: new Date().toISOString(),
      isOwn: true
    };

    setMessages([...messages, message]);
    setNewMessage('');

    // Send to backend
    try {
      const API_ENDPOINT = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/trip-planner';
      await fetch(`${API_ENDPOINT}/trips/${tripId}/chat/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: message.text }),
      });
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(diff / 3600000);
    const days = Math.floor(diff / 86400000);

    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes}m ago`;
    if (hours < 24) return `${hours}h ago`;
    if (days < 7) return `${days}d ago`;
    return date.toLocaleDateString();
  };

  if (loading) {
    return (
      <div className="trip-chat-page">
        <div className="chat-loading">Loading chat...</div>
      </div>
    );
  }

  return (
    <div className="trip-chat-page">
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate('/trips')}>
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M19 12H5M5 12L12 19M5 12L12 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Back
        </button>
        <div className="chat-header-info">
          <h1>Trip Chat</h1>
          <div className="participants-list">
            {participants.map((p) => (
              <span key={p.id} className="participant-tag">
                {p.name}
              </span>
            ))}
          </div>
        </div>
      </div>

      {tripInfo && (
        <div className="trip-info-card">
          <h3>Original Trip Request</h3>
          <p>{tripInfo.prompt}</p>
        </div>
      )}

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="chat-empty">
            <p>No messages yet. Start the conversation!</p>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.isOwn ? 'message-own' : 'message-other'}`}
            >
              {!message.isOwn && (
                <div className="message-avatar">
                  {participants.find(p => p.name === message.sender)?.avatar ? (
                    <img
                      src={participants.find(p => p.name === message.sender).avatar}
                      alt={message.sender}
                    />
                  ) : (
                    <div className="avatar-placeholder">
                      {message.sender.charAt(0)}
                    </div>
                  )}
                </div>
              )}
              <div className="message-content">
                {!message.isOwn && <div className="message-sender">{message.sender}</div>}
                <div className="message-bubble">
                  <p>{message.text}</p>
                  <span className="message-time">{formatTime(message.timestamp)}</span>
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      <form className="chat-input-form" onSubmit={handleSendMessage}>
        <input
          ref={inputRef}
          type="text"
          className="chat-input"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button type="submit" className="send-button" disabled={!newMessage.trim()}>
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M22 2L11 13M22 2L15 22L11 13M22 2L2 9L11 13" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>
      </form>
    </div>
  );
};

export default TripChat;
