import React, { useState, useEffect } from 'react';
import './LocationsGrid.css';

const LocationsGrid = () => {
  // Comprehensive list of 27+ unique locations (9 per row, 3 rows)
  const allLocations = [
    // Row 1 locations (9 unique)
    { id: 1, name: 'Bali, Indonesia', image: 'https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=600&h=400&fit=crop', country: 'Indonesia' },
    { id: 2, name: 'Santorini, Greece', image: 'https://images.unsplash.com/photo-1613395877344-13d4a8e0d49e?w=600&h=400&fit=crop', country: 'Greece' },
    { id: 3, name: 'Tokyo, Japan', image: 'https://images.unsplash.com/photo-1540959733332-eab4deabeeaf?w=600&h=400&fit=crop', country: 'Japan' },
    { id: 4, name: 'Paris, France', image: 'https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=600&h=400&fit=crop', country: 'France' },
    { id: 5, name: 'Maldives', image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=400&fit=crop', country: 'Maldives' },
    { id: 6, name: 'Dubai, UAE', image: 'https://images.unsplash.com/photo-1512453979798-5e2661e2e0e4?w=600&h=400&fit=crop', country: 'UAE' },
    { id: 7, name: 'New York, USA', image: 'https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9?w=600&h=400&fit=crop', country: 'USA' },
    { id: 8, name: 'Rome, Italy', image: 'https://images.unsplash.com/photo-1529260830199-42c24126f198?w=600&h=400&fit=crop', country: 'Italy' },
    { id: 9, name: 'Barcelona, Spain', image: 'https://images.unsplash.com/photo-1539037116277-4db20889f2d4?w=600&h=400&fit=crop', country: 'Spain' },
    
    // Row 2 locations (9 unique)
    { id: 10, name: 'Sydney, Australia', image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=400&fit=crop', country: 'Australia' },
    { id: 11, name: 'Iceland', image: 'https://images.unsplash.com/photo-1531168556467-80aace0d0144?w=600&h=400&fit=crop', country: 'Iceland' },
    { id: 12, name: 'Thailand', image: 'https://images.unsplash.com/photo-1552465011-b4e21bf6e79c?w=600&h=400&fit=crop', country: 'Thailand' },
    { id: 13, name: 'Switzerland', image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=400&fit=crop', country: 'Switzerland' },
    { id: 14, name: 'Morocco', image: 'https://images.unsplash.com/photo-1539020140153-e479b8c22e70?w=600&h=400&fit=crop', country: 'Morocco' },
    { id: 15, name: 'Norway', image: 'https://images.unsplash.com/photo-1469474968028-56623f02e42e?w=600&h=400&fit=crop', country: 'Norway' },
    { id: 16, name: 'London, UK', image: 'https://images.unsplash.com/photo-1513635269975-59663e0ac1ad?w=600&h=400&fit=crop', country: 'UK' },
    { id: 17, name: 'Amsterdam, Netherlands', image: 'https://images.unsplash.com/photo-1534351590666-13e3e96b5017?w=600&h=400&fit=crop', country: 'Netherlands' },
    { id: 18, name: 'Prague, Czech Republic', image: 'https://images.unsplash.com/photo-1541849546-216549ae216d?w=600&h=400&fit=crop', country: 'Czech Republic' },
    
    // Row 3 locations (9 unique)
    { id: 19, name: 'Vienna, Austria', image: 'https://images.unsplash.com/photo-1516550893923-42d28e5677af?w=600&h=400&fit=crop', country: 'Austria' },
    { id: 20, name: 'Venice, Italy', image: 'https://images.unsplash.com/photo-1514890547357-a9ee19272597?w=600&h=400&fit=crop', country: 'Italy' },
    { id: 21, name: 'Seoul, South Korea', image: 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=600&h=400&fit=crop', country: 'South Korea' },
    { id: 22, name: 'Singapore', image: 'https://images.unsplash.com/photo-1525625293386-3f8f99389edd?w=600&h=400&fit=crop', country: 'Singapore' },
    { id: 23, name: 'Hong Kong', image: 'https://images.unsplash.com/photo-1536599018102-9f803c140fc1?w=600&h=400&fit=crop', country: 'Hong Kong' },
    { id: 24, name: 'Cairo, Egypt', image: 'https://images.unsplash.com/photo-1539650116574-75c0c6d73a6e?w=600&h=400&fit=crop', country: 'Egypt' },
    { id: 25, name: 'Cape Town, South Africa', image: 'https://images.unsplash.com/photo-1505142468610-359e7d316be0?w=600&h=400&fit=crop', country: 'South Africa' },
    { id: 26, name: 'Rio de Janeiro, Brazil', image: 'https://images.unsplash.com/photo-1483729558449-99ef09a8c325?w=600&h=400&fit=crop', country: 'Brazil' },
    { id: 27, name: 'Machu Picchu, Peru', image: 'https://images.unsplash.com/photo-1526392060635-9d6019884377?w=600&h=400&fit=crop', country: 'Peru' },
    
    // Additional locations for rotation (9 more)
    { id: 28, name: 'Bangkok, Thailand', image: 'https://images.unsplash.com/photo-1552465011-b4e21bf6e79c?w=600&h=400&fit=crop', country: 'Thailand' },
    { id: 29, name: 'Mumbai, India', image: 'https://images.unsplash.com/photo-1529253355930-ddbe423a2ac7?w=600&h=400&fit=crop', country: 'India' },
    { id: 30, name: 'Kyoto, Japan', image: 'https://images.unsplash.com/photo-1493976040374-85c8e12f0c0e?w=600&h=400&fit=crop', country: 'Japan' },
    { id: 31, name: 'Lisbon, Portugal', image: 'https://images.unsplash.com/photo-1555881403-671085d7e8e4?w=600&h=400&fit=crop', country: 'Portugal' },
    { id: 32, name: 'Istanbul, Turkey', image: 'https://images.unsplash.com/photo-1524231757912-21f4fe3a7200?w=600&h=400&fit=crop', country: 'Turkey' },
    { id: 33, name: 'Budapest, Hungary', image: 'https://images.unsplash.com/photo-1555993530-7d0a0c0e8b5e?w=600&h=400&fit=crop', country: 'Hungary' },
    { id: 34, name: 'Edinburgh, Scotland', image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600&h=400&fit=crop', country: 'Scotland' },
    { id: 35, name: 'Stockholm, Sweden', image: 'https://images.unsplash.com/photo-1539037116277-4db20889f2d4?w=600&h=400&fit=crop', country: 'Sweden' },
    { id: 36, name: 'Copenhagen, Denmark', image: 'https://images.unsplash.com/photo-1513622470522-26c3c8a854bc?w=600&h=400&fit=crop', country: 'Denmark' },
  ];

  // Initialize each row with its own starting index
  const [rowIndices, setRowIndices] = useState({
    row1: 0,
    row2: 9,
    row3: 18
  });

  // Get 9 locations for a specific row starting from a given index
  const getRowLocations = (startIndex) => {
    const locations = [];
    for (let i = 0; i < 9; i++) {
      const index = (startIndex + i) % allLocations.length;
      locations.push(allLocations[index]);
    }
    return locations;
  };

  // Rotate each row independently
  useEffect(() => {
    const interval = setInterval(() => {
      setRowIndices(prev => ({
        row1: (prev.row1 + 1) % allLocations.length,
        row2: (prev.row2 + 1) % allLocations.length,
        row3: (prev.row3 + 1) % allLocations.length,
      }));
    }, 3000); // Rotate every 3 seconds

    return () => clearInterval(interval);
  }, [allLocations.length]);

  const row1Locations = getRowLocations(rowIndices.row1);
  const row2Locations = getRowLocations(rowIndices.row2);
  const row3Locations = getRowLocations(rowIndices.row3);

  return (
    <div className="locations-grid-container">
      <h2 className="locations-title">Explore Amazing Destinations</h2>
      <p className="locations-subtitle">Discover the world's most beautiful places</p>
      
      <div className="locations-grid">
        {/* Row 1 */}
        <div className="location-row">
          {row1Locations.map((location) => (
            <div key={location.id} className="location-card">
              <div className="location-image-wrapper">
                <img 
                  src={location.image} 
                  alt={location.name}
                  className="location-image"
                  loading="lazy"
                />
                <div className="location-overlay">
                  <h3 className="location-name">{location.name}</h3>
                  <p className="location-country">{location.country}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Row 2 */}
        <div className="location-row">
          {row2Locations.map((location) => (
            <div key={location.id} className="location-card">
              <div className="location-image-wrapper">
                <img 
                  src={location.image} 
                  alt={location.name}
                  className="location-image"
                  loading="lazy"
                />
                <div className="location-overlay">
                  <h3 className="location-name">{location.name}</h3>
                  <p className="location-country">{location.country}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Row 3 */}
        <div className="location-row">
          {row3Locations.map((location) => (
            <div key={location.id} className="location-card">
              <div className="location-image-wrapper">
                <img 
                  src={location.image} 
                  alt={location.name}
                  className="location-image"
                  loading="lazy"
                />
                <div className="location-overlay">
                  <h3 className="location-name">{location.name}</h3>
                  <p className="location-country">{location.country}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LocationsGrid;
