import React, { useState, useRef, useEffect } from 'react';
import './SearchBar.css';

const SearchBar = () => {
  const [prompt, setPrompt] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const textareaRef = useRef(null);
  const wrapperRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (prompt.trim() && !isGenerating) {
      setIsGenerating(true);
      console.log('AI Agent Prompt:', prompt);
      
      // TODO: Replace with actual AI agent API call
      // This will generate comprehensive itinerary including:
      // - Travel arrangements (flights, trains, etc.)
      // - Accommodation recommendations
      // - Medical requirements and vaccinations
      // - Weather-based plan adjustments
      // - Local attractions and activities
      // - Dining recommendations
      // - Transportation options
      // - Emergency contacts
      // - Cultural tips and requirements
      
      try {
        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 2000));
        
        // Handle response here
        console.log('Itinerary generated successfully');
        
        // Reset form
        setPrompt('');
      } catch (error) {
        console.error('Error generating itinerary:', error);
      } finally {
        setIsGenerating(false);
      }
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className="search-container">
      <div className="search-header">
        <h2 className="ai-planner-title">AI Trip Planner</h2>
        <p className="ai-planner-subtitle">
          Travel smarter with AI that plans it all â€” flights, hotels, health, and weather, personalized just for you. One platform, every detail, zero hassle.
        </p>
      </div>
      
      <form className="search-form" onSubmit={handleSubmit}>
        <div 
          className={`search-wrapper ${isFocused ? 'focused' : ''} ${isGenerating ? 'generating' : ''}`}
          onClick={(e) => {
            // If clicking on the wrapper (not on textarea or button), focus the textarea
            if (e.target === e.currentTarget || e.target.classList.contains('search-wrapper')) {
              e.currentTarget.querySelector('.search-input')?.focus();
            }
          }}
        >
          <textarea
            className="search-input"
            placeholder="Describe your travel plans in detail... For example: I'm planning a 10-day trip to Japan in March 2024. I have diabetes and need to ensure medical facilities are accessible. I prefer budget-friendly hotels near public transportation. Please include weather-based backup plans for outdoor activities, recommendations for what to pack, and shopping suggestions for items to buy before and after the trip. Also, I'd like information about cultural etiquette and any health requirements or vaccinations needed."
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            onFocus={() => setIsFocused(true)}
            onBlur={() => setIsFocused(false)}
            onKeyDown={handleKeyDown}
            rows={6}
            disabled={isGenerating}
            style={{ width: '100%' }}
          />
          <div className="search-actions">
            <div className="char-count">
              {prompt.length > 0 && (
                <span className={prompt.length > 2000 ? 'char-count-warning' : ''}>
                  {prompt.length} characters
                </span>
              )}
            </div>
            <button 
              type="submit" 
              className="search-button"
              disabled={!prompt.trim() || isGenerating}
              title="Generate itinerary (Enter)"
            >
              {isGenerating ? (
                <div className="spinner"></div>
              ) : (
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              )}
            </button>
          </div>
        </div>
        <div className="search-hint">
          <p>ðŸ’¡ <strong>Tip:</strong> The more details you provide, the better your personalized itinerary will be. Include dates, preferences, medical needs, budget, and any special requirements.</p>
        </div>
      </form>
    </div>
  );
};

export default SearchBar;
