import React, { useState } from 'react';
import './SearchBar.css';

const SearchBar = () => {
  const [prompt, setPrompt] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (prompt.trim() && !isGenerating) {
      setIsGenerating(true);
      console.log('AI Agent Prompt:', prompt);
      
      // TODO: Replace with actual AI agent API call
      // This will generate comprehensive itinerary including:
      // - Travel arrangements (flights, trains, etc.)
      // - Accommodation recommendations
      // - Medical requirements and vaccinations
      // - Weather-based plan adjustments
      // - Local attractions and activities
      // - Dining recommendations
      // - Transportation options
      // - Emergency contacts
      // - Cultural tips and requirements
      
      try {
        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 2000));
        
        // Handle response here
        console.log('Itinerary generated successfully');
        
        // Reset form
        setPrompt('');
      } catch (error) {
        console.error('Error generating itinerary:', error);
      } finally {
        setIsGenerating(false);
      }
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className="search-container">
      <div className="search-header">
        <h2 className="ai-planner-title">AI Trip Planner</h2>
        <p className="ai-planner-subtitle">
          Travel smarter with AI that plans it all â€” flights, hotels, health, and weather, personalized just for you. One platform, every detail, zero hassle.
        </p>
      </div>
      
      <form className="search-form" onSubmit={handleSubmit}>
        <div className={`search-wrapper ${isFocused ? 'focused' : ''} ${isGenerating ? 'generating' : ''}`}>
          <textarea
            className="search-input"
            placeholder="Describe your trip: e.g., 'Plan a 10-day trip to Japan in March, I have diabetes and prefer budget hotels. Include weather-based backup plans.'"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            onFocus={() => setIsFocused(true)}
            onBlur={() => setIsFocused(false)}
            onKeyDown={handleKeyDown}
            rows={1}
            disabled={isGenerating}
          />
          <button 
            type="submit" 
            className="search-button"
            disabled={!prompt.trim() || isGenerating}
          >
            {isGenerating ? (
              <div className="spinner"></div>
            ) : (
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M22 22L20 20M11.5 21C16.7467 21 21 16.7467 21 11.5C21 6.25329 16.7467 2 11.5 2C6.25329 2 2 6.25329 2 11.5C2 16.7467 6.25329 21 11.5 21Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            )}
          </button>
        </div>
      </form>
      
      <div className="search-suggestions">
        <p className="suggestions-text">
          <strong>Examples:</strong> "7-day European tour with medical insurance", 
          "Family trip to Bali with weather backup plans", 
          "Solo travel to Iceland with accommodation and transport"
        </p>
      </div>
    </div>
  );
};

export default SearchBar;
