    },
    {
      id: 6,
      name: 'Dubai, UAE',
      image: 'https://images.unsplash.com/photo-1512453979798-5e2661e2e0e4?w=800&h=600&fit=crop',
      description: 'Luxury and modern architecture'
    },
    {
      id: 7,
      name: 'New York, USA',
      image: 'https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9?w=800&h=600&fit=crop',
      description: 'The city that never sleeps'
    },
    {
      id: 8,
      name: 'Rome, Italy',
      image: 'https://images.unsplash.com/photo-1529260830199-42c24126f198?w=800&h=600&fit=crop',
      description: 'Eternal city with rich history'
    },
    {
      id: 9,
      name: 'Barcelona, Spain',
      image: 'https://images.unsplash.com/photo-1539037116277-4db20889f2d4?w=800&h=600&fit=crop',
      description: 'Vibrant culture and stunning architecture'
    },
    {
      id: 10,
      name: 'Sydney, Australia',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
      description: 'Harbor city with iconic landmarks'
    },
    {
      id: 11,
      name: 'Iceland',
      image: 'https://images.unsplash.com/photo-1531168556467-80aace0d0144?w=800&h=600&fit=crop',
      description: 'Land of fire and ice'
    },
    {
      id: 12,
      name: 'Thailand',
      image: 'https://images.unsplash.com/photo-1552465011-b4e21bf6e79c?w=800&h=600&fit=crop',
      description: 'Golden temples and tropical beaches'
    },
    {
      id: 13,
