      image: 'https://images.unsplash.com/photo-1539020140153-e479b8c22e70?w=800&h=600&fit=crop',
      description: 'Exotic markets and desert landscapes'
    },
    {
      id: 15,
      name: 'Norway',
      image: 'https://images.unsplash.com/photo-1469474968028-56623f02e42e?w=800&h=600&fit=crop',
      description: 'Fjords and Northern Lights'
    },
    {
      id: 16,
      name: 'London, UK',
      image: 'https://images.unsplash.com/photo-1513635269975-59663e0ac1ad?w=800&h=600&fit=crop',
      description: 'Historic capital with royal heritage'
    },
    {
      id: 17,
      name: 'Amsterdam, Netherlands',
      image: 'https://images.unsplash.com/photo-1534351590666-13e3e96b5017?w=800&h=600&fit=crop',
      description: 'Canals, art, and cycling culture'
    },
    {
      id: 18,
      name: 'Prague, Czech Republic',
      image: 'https://images.unsplash.com/photo-1541849546-216549ae216d?w=800&h=600&fit=crop',
      description: 'Medieval charm and Gothic architecture'
    },
    {
      id: 19,
      name: 'Vienna, Austria',
      image: 'https://images.unsplash.com/photo-1516550893923-42d28e5677af?w=800&h=600&fit=crop',
      description: 'Imperial elegance and classical music'
    },
    {
      id: 20,
      name: 'Venice, Italy',
      image: 'https://images.unsplash.com/photo-1514890547357-a9ee19272597?w=800&h=600&fit=crop',
      description: 'Romantic canals and Renaissance art'
    },
    {
      id: 21,
      name: 'Seoul, South Korea',
      image: 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=800&h=600&fit=crop',
      description: 'Modern tech hub with ancient palaces'
    },
    {
      id: 22,
      name: 'Singapore',
      image: 'https://images.unsplash.com/photo-1525625293386-3f8f99389edd?w=800&h=600&fit=crop',
      description: 'Garden city with futuristic skyline'
    },
    {
      id: 23,
      name: 'Hong Kong',
      image: 'https://images.unsplash.com/photo-1536599018102-9f803c140fc1?w=800&h=600&fit=crop',
      description: 'Vibrant cityscape and culinary paradise'
    },
    {
      id: 24,
      name: 'Cairo, Egypt',
      image: 'https://images.unsplash.com/photo-1539650116574-75c0c6d73a6e?w=800&h=600&fit=crop',
      description: 'Ancient pyramids and rich history'
    },
    {
      id: 25,
      name: 'Cape Town, South Africa',
      image: 'https://images.unsplash.com/photo-1505142468610-359e7d316be0?w=800&h=600&fit=crop',
      description: 'Stunning coastline and Table Mountain'
    },
    {
      id: 26,
      name: 'Rio de Janeiro, Brazil',
      image: 'https://images.unsplash.com/photo-1483729558449-99ef09a8c325?w=800&h=600&fit=crop',
      description: 'Vibrant beaches and carnival spirit'
    },
    {
      id: 27,
      name: 'Machu Picchu, Peru',
      image: 'https://images.unsplash.com/photo-1526392060635-9d6019884377?w=800&h=600&fit=crop',
      description: 'Ancient Incan citadel in the clouds'
    },
    {
      id: 28,
      name: 'Bangkok, Thailand',
      image: 'https://images.unsplash.com/photo-1552465011-b4e21bf6e79c?w=800&h=600&fit=crop',
      description: 'Temples, street food, and vibrant nightlife'
    },
    {
      id: 29,
      name: 'Mumbai, India',
      image: 'https://images.unsplash.com/photo-1529253355930-ddbe423a2ac7?w=800&h=600&fit=crop',
      description: 'Bollywood city with diverse culture'
    },
    {
      id: 30,
      name: 'Kyoto, Japan',
      image: 'https://images.unsplash.com/photo-1493976040374-85c8e12f0c0e?w=800&h=600&fit=crop',
      description: 'Traditional temples and cherry blossoms'
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => {
        // Move to next set of 3 destinations
        const nextIndex = prevIndex + 3;
        // If we've reached the end, loop back to start
        return nextIndex >= destinations.length ? 0 : nextIndex;
      });
    }, 4000); // Rotate every 4 seconds

    return () => clearInterval(interval);
  }, [destinations.length]);

  // Get current 3 destinations to display
  const getCurrentDestinations = () => {
    const destinationsToShow = [];
    for (let i = 0; i < 3; i++) {
      const index = (currentIndex + i) % destinations.length;
      destinationsToShow.push(destinations[index]);
    }
    return destinationsToShow;
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => {
      const nextIndex = prevIndex + 3;
      return nextIndex >= destinations.length ? 0 : nextIndex;
    });
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => {
      const prevIndexNew = prevIndex - 3;
      return prevIndexNew < 0 ? destinations.length - (destinations.length % 3 || 3) : prevIndexNew;
    });
  };

  const currentDestinations = getCurrentDestinations();

  return (
    <div className="hero-banner">
      <div className="banner-header">
        <h2 className="banner-title">Most Popular Destinations</h2>
      </div>
      <div className="banner-container">
        <button className="banner-nav prev" onClick={goToPrevious}>
          ‹
        </button>
        
        <div className="destinations-grid">
          {currentDestinations.map((destination, idx) => (
            <div key={destination.id} className="destination-card">
              <div className="card-image-wrapper">
                <img 
                  src={destination.image} 
                  alt={destination.name}
                  className="card-image"
                />
                <div className="card-overlay">
                  <h3 className="card-name">{destination.name}</h3>
                  <p className="card-description">{destination.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        <button className="banner-nav next" onClick={goToNext}>
          ›
        </button>

        <div className="banner-indicators">
          {Array.from({ length: Math.ceil(destinations.length / 3) }).map((_, index) => {
            const isActive = Math.floor(currentIndex / 3) === index;
            return (
              <button
                key={index}
                className={`indicator ${isActive ? 'active' : ''}`}
                onClick={() => setCurrentIndex(index * 3)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default HeroBanner;
