import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [selectedCountry, setSelectedCountry] = useState('US');
  const [selectedLanguage, setSelectedLanguage] = useState('English');
  const [selectedCurrency, setSelectedCurrency] = useState('USD');

  const countries = [
    { value: 'US', label: 'United States' },
    { value: 'UK', label: 'United Kingdom' },
    { value: 'CA', label: 'Canada' },
    { value: 'AU', label: 'Australia' },
    { value: 'DE', label: 'Germany' },
    { value: 'FR', label: 'France' },
  ];

  const languages = [
    { value: 'English', label: 'English' },
    { value: 'Spanish', label: 'Español' },
    { value: 'French', label: 'Français' },
    { value: 'German', label: 'Deutsch' },
    { value: 'Italian', label: 'Italiano' },
  ];

  const currencies = [
    { value: 'USD', label: 'USD ($)' },
    { value: 'EUR', label: 'EUR (€)' },
    { value: 'GBP', label: 'GBP (£)' },
    { value: 'JPY', label: 'JPY (¥)' },
    { value: 'CAD', label: 'CAD (C$)' },
  ];

  return (
    <header className="header">
      <div className="header-container">
        <div className="logo-container">
          <div className="logo">
            <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M20 5L25 15L35 17L28 25L29 35L20 30L11 35L12 25L5 17L15 15L20 5Z" fill="#FF6B35"/>
              <path d="M20 10L23 17L30 18L25 23L26 30L20 27L14 30L15 23L10 18L17 17L20 10Z" fill="#F7931E"/>
              <circle cx="20" cy="20" r="3" fill="#FFD23F"/>
            </svg>
          </div>
          <span className="logo-text">Phoenix Travel</span>
        </div>

        <div className="header-right">
          <div className="dropdown-group">
            <select 
              className="dropdown" 
              value={selectedCountry}
              onChange={(e) => setSelectedCountry(e.target.value)}
            >
              {countries.map(country => (
                <option key={country.value} value={country.value}>
                  {country.label}
                </option>
              ))}
            </select>

            <select 
              className="dropdown" 
              value={selectedLanguage}
              onChange={(e) => setSelectedLanguage(e.target.value)}
            >
              {languages.map(lang => (
                <option key={lang.value} value={lang.value}>
                  {lang.label}
                </option>
              ))}
            </select>

            <select 
              className="dropdown" 
              value={selectedCurrency}
              onChange={(e) => setSelectedCurrency(e.target.value)}
            >
              {currencies.map(currency => (
                <option key={currency.value} value={currency.value}>
                  {currency.label}
                </option>
              ))}
            </select>
          </div>

          <div className="auth-buttons">
            <button className="btn-login">Login</button>
            <button className="btn-signup">Create Account</button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
