import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getTripsFromLocalStorage } from '../utils/tripUtils';
import './TripChat.css';

const TripChat = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [tripInfo, setTripInfo] = useState(null);
  const [sharedUsers, setSharedUsers] = useState([]);
  const [showInviteModal, setShowInviteModal] = useState(false);
  const [inviteEmail, setInviteEmail] = useState('');
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const API_ENDPOINT = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/trip-planner';

  // Fetch trip info and generate initial plan
  useEffect(() => {
    let isMounted = true;
    
    const fetchTripDataAndGeneratePlan = async () => {
      try {
        // First, try to get trip from localStorage
        const localTrips = getTripsFromLocalStorage();
        const localTrip = localTrips.find(trip => trip.id === tripId);
        
        if (localTrip && isMounted) {
          setTripInfo({
            id: localTrip.id,
            title: localTrip.title,
            destination: localTrip.destination,
            image: localTrip.image,
            prompt: localTrip.prompt
          });
        }

        // Try to fetch from backend API
        try {
          const tripResponse = await fetch(`${API_ENDPOINT}/trips/${tripId}?userId=Kartik7`);
          if (tripResponse.ok && isMounted) {
            const tripData = await tripResponse.json();
            setTripInfo(prev => prev || tripData);
          }
        } catch (err) {
          console.log('Could not fetch from backend, using local data');
        }

        // Fetch existing messages
        let hasExistingMessages = false;
        try {
          const messagesResponse = await fetch(`${API_ENDPOINT}/trips/${tripId}/messages?userId=Kartik7`);
          if (messagesResponse.ok) {
            const messagesData = await messagesResponse.json();
            if (messagesData.messages && messagesData.messages.length > 0 && isMounted) {
              setMessages(messagesData.messages);
              hasExistingMessages = true;
            }
          }
        } catch (err) {
          console.log('Could not fetch messages from backend');
        }

        // If no messages exist and we have a prompt, generate the initial plan
        if (!hasExistingMessages) {
          const tripPrompt = localTrip?.prompt;
          if (tripPrompt && isMounted) {
            await generateInitialTravelPlan(tripPrompt);
          }
        }
      } catch (error) {
        console.error('Error fetching trip data:', error);
        if (!isMounted) return;
        
        // Use fallback data
        const localTrips = getTripsFromLocalStorage();
        const localTrip = localTrips.find(trip => trip.id === tripId);
        if (localTrip && isMounted) {
          setTripInfo({
            id: localTrip.id,
            title: localTrip.title,
            destination: localTrip.destination,
            image: localTrip.image,
            prompt: localTrip.prompt
          });
          // Generate plan with local trip prompt
          if (localTrip.prompt) {
            await generateInitialTravelPlan(localTrip.prompt);
          }
        } else if (isMounted) {
          setTripInfo({
            id: tripId,
            title: `Trip ${tripId}`,
            destination: 'Unknown',
            image: 'https://images.unsplash.com/photo-1488646953014-85cb44e25828?w=800&h=400&fit=crop'
          });
        }
      }
    };

    fetchTripDataAndGeneratePlan();
    
    return () => {
      isMounted = false;
    };
  }, [tripId]);

  // Generate initial comprehensive travel plan using ChatGPT
  const generateInitialTravelPlan = async (prompt) => {
    setIsLoading(true);
    
    const systemPrompt = `You are an expert travel planning AI assistant. Your role is to help users create comprehensive, personalized travel itineraries. 

When a user provides a travel request:
1. Analyze the request carefully
2. If you need more information (dates, budget, preferences, medical needs, etc.), ask specific questions
3. Once you have enough information, create a detailed day-by-day itinerary including:
   - Travel/Transportation for each day
   - Hotels/Accommodations with locations
   - Restaurants (breakfast, lunch, dinner) for each day
   - Shopping recommendations (before and during trip)
   - Medical requirements (vaccinations, medications, health precautions)
   - Weather forecast for each day
   - Day-by-day activities and recommendations

Format your responses clearly with bullet points and organized sections. Be conversational and ask follow-up questions when needed.`;

    const userPrompt = `I need help planning a trip. Here's my initial request: "${prompt}"

Please help me create a comprehensive travel plan. If you need any additional information to create the best itinerary, please ask me specific questions.`;

    try {
      // Save user's initial prompt as first message
      const initialUserMessage = {
        id: Date.now(),
        role: 'user',
        content: prompt,
        timestamp: new Date().toISOString()
      };
      
      // Save to backend
      await saveMessageToBackend(initialUserMessage);

      // Call ChatGPT API
      const response = await fetch(`${API_ENDPOINT}/trips/${tripId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: 'Kartik7',
          tripId: tripId,
          message: userPrompt,
          systemPrompt: systemPrompt,
          conversationHistory: [],
          timestamp: new Date().toISOString(),
          isInitialPlan: true
        })
      });

      if (response.ok) {
        const data = await response.json();
        const aiMessage = {
          id: Date.now() + 1,
          role: 'assistant',
          content: data.response || data.message || data.plan || 'I\'m analyzing your travel request. Let me ask a few questions to create the perfect itinerary for you...',
          timestamp: new Date().toISOString()
        };
        
        // Save AI response to backend
        await saveMessageToBackend(aiMessage);
        
        setMessages([initialUserMessage, aiMessage]);
      } else {
        // Fallback: Create a sample plan structure
        const samplePlan = createSamplePlan(prompt);
        const aiMessage = {
          id: Date.now() + 1,
          role: 'assistant',
          content: samplePlan,
          timestamp: new Date().toISOString()
        };
        await saveMessageToBackend(aiMessage);
        setMessages([initialUserMessage, aiMessage]);
      }
    } catch (error) {
      console.error('Error generating travel plan:', error);
      // Fallback: Create a sample plan structure
      const initialUserMessage = {
        id: Date.now(),
        role: 'user',
        content: prompt,
        timestamp: new Date().toISOString()
      };
      const samplePlan = createSamplePlan(prompt);
      const aiMessage = {
        id: Date.now() + 1,
        role: 'assistant',
        content: samplePlan,
        timestamp: new Date().toISOString()
      };
      await saveMessageToBackend(initialUserMessage).catch(() => {});
      await saveMessageToBackend(aiMessage).catch(() => {});
      setMessages([initialUserMessage, aiMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Save message to backend
  const saveMessageToBackend = async (message) => {
    try {
      await fetch(`${API_ENDPOINT}/trips/${tripId}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: 'Kartik7',
          tripId: tripId,
          message: message,
          timestamp: message.timestamp
        })
      });
    } catch (error) {
      console.error('Error saving message to backend:', error);
      // Also save to localStorage as backup
      saveMessageToLocalStorage(message);
    }
  };

  // Save message to localStorage as backup
  const saveMessageToLocalStorage = (message) => {
    try {
      const key = `trip_chat_${tripId}`;
      const existingMessages = JSON.parse(localStorage.getItem(key) || '[]');
      existingMessages.push(message);
      localStorage.setItem(key, JSON.stringify(existingMessages));
    } catch (error) {
      console.error('Error saving message to localStorage:', error);
    }
  };

  // Create a sample plan structure if API fails
  const createSamplePlan = (prompt) => {
    return `# Your Personalized Travel Plan

Based on your request: "${prompt}"

## Pre-Trip Preparation

### Medical Requirements
• Consult with your doctor 4-6 weeks before travel
• Check required vaccinations for your destination
• Pack prescription medications in original containers
• Bring copies of prescriptions and medical records
• Consider travel insurance with medical coverage

### Shopping Before Trip
• Travel adapter and converter
• Portable charger/power bank
• Travel-sized toiletries
• Comfortable walking shoes
• Weather-appropriate clothing
• Travel documents organizer

---

## Day-by-Day Itinerary

### Day 1: Arrival
**Travel:**
• Flight arrival at [Destination Airport]
• Airport transfer to hotel

**Hotel:**
• Check-in at [Recommended Hotel]
• Location: [Area/District]

**Restaurants:**
• Breakfast: [Local Café]
• Lunch: [Traditional Restaurant]
• Dinner: [Fine Dining Option]

**Weather Forecast:**
• Temperature: [Range]°C
• Conditions: [Sunny/Cloudy/Rainy]
• Pack: [Appropriate clothing]

**Shopping:**
• Visit [Local Market] for essentials
• Purchase [Local SIM card/Transportation pass]

---

### Day 2: Exploration
**Travel:**
• Public transportation pass
• Walking tours

**Hotel:**
• Continue stay at [Hotel Name]

**Restaurants:**
• Breakfast: [Hotel breakfast]
• Lunch: [Street food/Market]
• Dinner: [Rooftop restaurant]

**Weather Forecast:**
• Temperature: [Range]°C
• Conditions: [Weather conditions]

**Shopping:**
• [Local souvenirs]
• [Traditional items]

---

*[Additional days will be generated based on your trip duration]*

---

## Post-Trip Recommendations

### Shopping After Trip
• [Local specialties to bring home]
• [Duty-free items]
• [Gifts for family/friends]

### Follow-up
• Review travel insurance claims if needed
• Share feedback on accommodations
• Plan your next adventure!

*This is a sample structure. Your actual plan will be customized based on your specific requirements and destination.*`;
  };

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Auto-focus input on mount
  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      role: 'user',
      content: inputMessage.trim(),
      timestamp: new Date().toISOString()
    };

    // Add user message immediately
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInputMessage('');
    setIsLoading(true);

    // Save user message to backend
    await saveMessageToBackend(userMessage);

    try {
      // Prepare conversation history for ChatGPT
      const conversationHistory = updatedMessages.map(msg => ({
        role: msg.role,
        content: msg.content
      }));

      const systemPrompt = `You are an expert travel planning AI assistant. Help users create comprehensive, personalized travel itineraries. Ask questions when you need more information. Provide detailed day-by-day plans with travel, hotels, restaurants, shopping, medical requirements, and weather forecasts.`;

      // Send message to ChatGPT API
      const response = await fetch(`${API_ENDPOINT}/trips/${tripId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: 'Kartik7',
          tripId: tripId,
          message: userMessage.content,
          systemPrompt: systemPrompt,
          conversationHistory: conversationHistory,
          timestamp: userMessage.timestamp
        })
      });

      if (response.ok) {
        const data = await response.json();
        // Add AI response
        const aiMessage = {
          id: Date.now() + 1,
          role: 'assistant',
          content: data.response || data.message || 'I received your message. How can I help you with your trip?',
          timestamp: new Date().toISOString()
        };
        
        // Save AI response to backend
        await saveMessageToBackend(aiMessage);
        
        setMessages(prev => [...prev, aiMessage]);
      } else {
        // Fallback response if API fails
        const errorMessage = {
          id: Date.now() + 1,
          role: 'assistant',
          content: 'I received your message. How can I help you with your trip planning?',
          timestamp: new Date().toISOString()
        };
        await saveMessageToBackend(errorMessage);
        setMessages(prev => [...prev, errorMessage]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      // Fallback response on error
      const errorMessage = {
        id: Date.now() + 1,
        role: 'assistant',
        content: 'I received your message. How can I help you with your trip planning?',
        timestamp: new Date().toISOString()
      };
      await saveMessageToBackend(errorMessage);
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Invite user to view chat
  const handleInviteUser = async (e) => {
    e.preventDefault();
    if (!inviteEmail.trim()) return;

    try {
      const response = await fetch(`${API_ENDPOINT}/trips/${tripId}/invite`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: 'Kartik7',
          tripId: tripId,
          inviteEmail: inviteEmail.trim()
        })
      });

      if (response.ok) {
        const data = await response.json();
        setSharedUsers(prev => [...prev, { email: inviteEmail.trim(), status: 'invited' }]);
        setInviteEmail('');
        setShowInviteModal(false);
        alert(`Invitation sent to ${inviteEmail.trim()}`);
      } else {
        alert('Failed to send invitation. Please try again.');
      }
    } catch (error) {
      console.error('Error inviting user:', error);
      alert('Failed to send invitation. Please try again.');
    }
  };

  // Fetch shared users
  useEffect(() => {
    const fetchSharedUsers = async () => {
      try {
        const response = await fetch(`${API_ENDPOINT}/trips/${tripId}/shared-users?userId=Kartik7`);
        if (response.ok) {
          const data = await response.json();
          setSharedUsers(data.sharedUsers || []);
        }
      } catch (error) {
        console.error('Error fetching shared users:', error);
      }
    };
    fetchSharedUsers();
  }, [tripId]);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(e);
    }
  };

  // Format message content for better display (markdown-like formatting)
  const formatMessageContent = (content) => {
    if (!content) return '';
    
    // Convert markdown-style formatting to HTML
    let formatted = content
      // Headers
      .replace(/^### (.*$)/gim, '<h3>$1</h3>')
      .replace(/^## (.*$)/gim, '<h2>$1</h2>')
      .replace(/^# (.*$)/gim, '<h1>$1</h1>')
      // Bold
      .replace(/\*\*(.*?)\*\*/gim, '<strong>$1</strong>')
      // Bullet points
      .replace(/^• (.*$)/gim, '<li>$1</li>')
      .replace(/^- (.*$)/gim, '<li>$1</li>')
      // Line breaks
      .replace(/\n/g, '<br>');
    
    // Wrap consecutive <li> tags in <ul>
    formatted = formatted.replace(/(<li>.*?<\/li>(?:<br>)?)+/g, (match) => {
      return '<ul>' + match.replace(/<br>/g, '') + '</ul>';
    });
    
    return formatted;
  };

  return (
    <div className="trip-chat-page">
      <button className="back-button" onClick={() => navigate('/trips')}>
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M19 12H5M5 12L12 19M5 12L12 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
        Back to My Trips
      </button>

      {tripInfo && (
        <div className="trip-chat-header">
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px', flex: 1 }}>
            <div className="trip-chat-header-image">
              <img src={tripInfo.image} alt={tripInfo.destination} />
            </div>
            <div className="trip-chat-header-info">
              <h2>{tripInfo.title || `Trip ${tripId}`}</h2>
              <p>{tripInfo.destination || 'Unknown Destination'}</p>
            </div>
          </div>
          <div className="trip-chat-header-actions">
            <button 
              className="invite-button"
              onClick={() => setShowInviteModal(true)}
              title="Invite user to view chat"
            >
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <circle cx="8.5" cy="7" r="4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M20 8v6M23 11h-6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
              Invite
            </button>
            {sharedUsers.length > 0 && (
              <div className="shared-users">
                <span className="shared-users-label">Shared with:</span>
                {sharedUsers.map((user, idx) => (
                  <span key={idx} className="shared-user-badge">{user.email}</span>
                ))}
              </div>
            )}
          </div>
        </div>
      )}

      {showInviteModal && (
        <div className="invite-modal-overlay" onClick={() => setShowInviteModal(false)}>
          <div className="invite-modal" onClick={(e) => e.stopPropagation()}>
            <h3>Invite User to View Chat</h3>
            <form onSubmit={handleInviteUser}>
              <input
                type="email"
                className="invite-email-input"
                placeholder="Enter email address"
                value={inviteEmail}
                onChange={(e) => setInviteEmail(e.target.value)}
                required
              />
              <div className="invite-modal-actions">
                <button type="button" onClick={() => setShowInviteModal(false)}>Cancel</button>
                <button type="submit">Send Invitation</button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="chat-container">
        <div className="chat-messages" ref={messagesEndRef}>
          {messages.length === 0 ? (
            <div className="chat-empty-state">
              <div className="empty-state-icon">
                <svg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </div>
              <h3>Start a conversation</h3>
              <p>Ask questions about your trip, request changes, or get recommendations.</p>
            </div>
          ) : (
            messages.map((message) => (
              <div key={message.id} className={`message ${message.role === 'user' ? 'user-message' : 'assistant-message'}`}>
                <div 
                  className="message-content"
                  dangerouslySetInnerHTML={{ __html: message.role === 'assistant' ? formatMessageContent(message.content) : message.content }}
                />
                <div className="message-timestamp">
                  {new Date(message.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </div>
              </div>
            ))
          )}
          {isLoading && (
            <div className="message assistant-message">
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <form className="chat-input-form" onSubmit={handleSendMessage}>
          <div className="chat-input-wrapper">
            <textarea
              ref={inputRef}
              className="chat-input"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Message your trip assistant..."
              rows="1"
              disabled={isLoading}
            />
            <button 
              type="submit" 
              className="chat-send-button"
              disabled={!inputMessage.trim() || isLoading}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M22 2L11 13M22 2L15 22L11 13M22 2L2 9L11 13" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TripChat;
